.popup-window-button-area {
  font-family: Helvetica, Open Sans, Arial, Lucida Grande, sans-serif;
  flex: 1 0 auto;
  padding: 1rem 1.5rem;
  background-color: #f6f6f6;
  display: flex;
  flex-direction: row;
}

.button-area-left {
  text-align: left;
  flex: 1 1 100%;
  white-space: nowrap;
}

.button-area-center {
  text-align: center;
  flex: 1 1 100%;
  white-space: nowrap;
}

.button-area-right {
  text-align: right;
  flex: 1 1 100%;
  white-space: nowrap;
}


