.checkbox-area {
  position: relative;
  padding-left: 30px;
  white-space: nowrap;
}

input[type="checkbox"] {
  display: none;
}

input[type="checkbox"] + label {
  color: #5fa8e0;
}

input[type="checkbox"] + label span {
    display:inline-block;
    width:16px;
    height:16px;
    border: 1px solid #047cc0;
    border-radius: 2px;
    padding: 2px;
    margin-right: 1em;
    vertical-align:middle;
    background: transparent;
    cursor: pointer;
}

input[type="checkbox"] + label span.checkbox-dark {
    border: 1px solid #56acf2;
}

input[type="checkbox"]:hover:not(:disabled) + label span {
  border: 1px solid #175d8d;
  background-color: #C0BFC0;
}

input[type="checkbox"]:hover:not(:disabled) + label span.checkbox-dark {
  border: 1px solid #009bef;
  background-color: rgb(69, 70, 70);
}

input[type="checkbox"]:active:not(:disabled) + label span {
  border: 1px solid #1c496d;
  background-color: #777677;
}

input[type="checkbox"]:active:not(:disabled) + label span.checkbox-dark {
  border: 1px solid #047cc0;
  background-color: rgb(51, 51, 52);
}

input[type="checkbox"]:disabled + label span {
  opacity: 0.6;
}

input[type="checkbox"] + label span img{
  position: absolute;
  display: none;
}

input[type="checkbox"]:checked + label span {
    background-color: #047cc0;
}

input[type="checkbox"]:checked + label span.checkbox-dark {
    background-color: #56acf2;
}

input[type="checkbox"]:checked:hover:not(:disabled) + label span {
    background-color: #175d8d;
}

input[type="checkbox"]:checked:hover:not(:disabled) + label span.checkbox-dark {
    background-color: #009bef;
}

input[type="checkbox"]:checked:active:not(:disabled) + label span {
    background-color: #1c496d;
}

input[type="checkbox"]:checked:active:not(:disabled) + label span.checkbox-dark {
    background-color: #047cc0;
}

input[type="checkbox"]:checked + label span img{
  display: inline;
}


