.top-header-hr {
  height: 4px;
  margin: 0;
  background-color: #047cc0;
  width: 100%;
  border: none;
  flex: none;
}

.top-hr:hover {
  background-color: #009bef;
}

.bottom-header {}

.bottom-header-dark {
  background-color: #1a354d;
}

.bottom-header-hr {
  margin: 0 2rem;
  height: 1px;
  background-color: #bdbabd;
  border: none;
  flex: none;
}

.bottom-header-hr-dark {
  background-color: #516577;
}

.popup-window-header-area {
  margin: 0;
  padding: 0;
  display: flex;
  flex: none;
  height: 50px;
  color: #777677;
}

.popup-window-header-area-dark {
  background-color: #1a354d;
  color: #C0BFC0;
}

.popup-window-header {
  margin-left: 2rem;
  font-size: 21px;
  float: left;
  flex: 1 1 auto;
  display: flex;
  align-items: center;
}

.close-button {
  height: 20px;
  width: 20px;
  margin: 10px;
  padding: 0;
  flex: none;
  box-sizing: border-box;
  background-color: transparent;
  transition: 0.5s;
  background: url(../../assets/images/close_normal.png) right top no-repeat;
}

.close-button:hover {
  background-image: url(../../assets/images/close_hover.png);
}

.close-button:active {
  background-image: url(../../assets/images/close_pressed.png);
}

.close-button-dark {
  background: url(../../assets/images/close_normal_dark.png) right top no-repeat;
}

.close-button-dark:hover {
  background-image: url(../../assets/images/close_hover_dark.png);
}

.close-button-dark:active {
  background-image: url(../../assets/images/close_pressed_dark.png);
}

.popup-window-popup {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  max-height: 80%;
  background: #fff;
  display: flex;
  flex-direction: column;
  box-shadow: 0 0 10px rgba(1, 1, 1, 0.14);
}

@media (min-width: 1450px) {
  .popup-window-popup {
    width: 45%;
    margin: 2em auto;
  }
}

@media (max-width: 1449px) and (min-width: 1200px) {
  .popup-window-popup {
    width: 55%;
    margin: 2em auto;
  }
}

@media (max-width: 1199px) and (min-width: 1020px) {
  .popup-window-popup {
    width: 70%;
    margin: 1.5em auto;
  }
}

@media (max-width: 1019px) {
  .popup-window-popup {
    width: 95%;
    margin: 1em auto;
  }
}

.popup-window-content {
  flex: 1 1 auto;
  overflow-y: auto;
  overflow-x: hidden;
  display: flex;
  flex-direction: column;
}

.popup-window-button {
  font-size: large;
  min-width: 5em;
  border-radius: 0.5em;
  padding: .5em 1em;
}

