.veil {
  display: none;
  position: absolute;
  opacity: 0.5;
  z-index: 1;
  background: gray;
  background: rgba(0,0,0,0.5);
  flex: 1 1 auto;
  flex-direction: column;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
}

.state-active {
  display: flex !important;
}

.veil > img {
  position: absolute;
  left: calc(50% - 21px);
  top: calc(50% - 21px);
  height: 42px;
  width: 42px;
  z-index: 2;
  transform: rotate(360deg);
}

.rotated {
  animation: swirl 3s linear infinite;
}

@keyframes swirl {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

